00196     private async Task MoveActiveIndexMatchedTextSpanUp()
00197     {
00198         var renderBatchLocal = RenderBatch;
00199         if (renderBatchLocal is null)
00200             return;
00201         
00202         var findOverlayPresentationModel = renderBatchLocal.Model.PresentationModelList.FirstOrDefault(
00203             x => x.TextEditorPresentationKey == FindOverlayPresentationFacts.PresentationKey);
00204 
00205         if (findOverlayPresentationModel is null)
00206             return;
00207 
00208         var completedCalculation = findOverlayPresentationModel.CompletedCalculation;
00209 
00210         if (completedCalculation is null)
00211             return;
00212 
00213         if (_activeIndexMatchedTextSpan is null)
00214         {
00215             _activeIndexMatchedTextSpan = completedCalculation.TextSpanList.Length - 1;
00216         }
00217         else
00218         {
00219             if (completedCalculation.TextSpanList.Length == 0)
00220             {
00221                 _activeIndexMatchedTextSpan = null;
00222             }
00223             else
00224             {
00225                 _activeIndexMatchedTextSpan--;
00226                 if (_activeIndexMatchedTextSpan <= -1)
00227                     _activeIndexMatchedTextSpan = completedCalculation.TextSpanList.Length - 1;
00228             }
00229         }
00230 
00231         await HandleActiveIndexMatchedTextSpanChanged();
00232     }
