00234     private async Task MoveActiveIndexMatchedTextSpanDown()
00235     {
00236         var renderBatchLocal = RenderBatch;
00237         if (renderBatchLocal is null)
00238             return;
00239         
00240         var findOverlayPresentationModel = renderBatchLocal.Model.PresentationModelList.FirstOrDefault(
00241             x => x.TextEditorPresentationKey == FindOverlayPresentationFacts.PresentationKey);
00242 
00243         if (findOverlayPresentationModel is null)
00244             return;
00245 
00246         var completedCalculation = findOverlayPresentationModel.CompletedCalculation;
00247 
00248         if (completedCalculation is null)
00249             return;
00250 
00251         if (_activeIndexMatchedTextSpan is null)
00252         {
00253             _activeIndexMatchedTextSpan = 0;
00254         }
00255         else
00256         {
00257             if (completedCalculation.TextSpanList.Length == 0)
00258             {
00259                 _activeIndexMatchedTextSpan = null;
00260             }
00261             else
00262             {
00263                 _activeIndexMatchedTextSpan++;
00264                 if (_activeIndexMatchedTextSpan >= completedCalculation.TextSpanList.Length)
00265                     _activeIndexMatchedTextSpan = 0;
00266             }
