00272     private Task HandleActiveIndexMatchedTextSpanChanged()
00273     {
00274         var renderBatchLocal = RenderBatch;
00275         if (renderBatchLocal is null)
00276             return Task.CompletedTask;
00277         
00278         TextEditorService.PostUnique(
00279             nameof(HandleActiveIndexMatchedTextSpanChanged),
00280             editContext =>
00281             {
00282                 var localActiveIndexMatchedTextSpan = _activeIndexMatchedTextSpan;
00283 
00284                 if (localActiveIndexMatchedTextSpan is null)
00285                     return Task.CompletedTask;
00286 
00287                 var viewModelModifier = editContext.GetViewModelModifier(renderBatchLocal.ViewModel.ViewModelKey);
00288 
00289                 if (viewModelModifier is null)
00290                     return Task.CompletedTask;
00291                 
00292                 var modelModifier = editContext.GetModelModifier(renderBatchLocal.Model.ResourceUri);
00293 
00294                 if (modelModifier is null)
00295                     return Task.CompletedTask;
00296 
00297                 var presentationModel = modelModifier.PresentationModelList.FirstOrDefault(x =>
00298                     x.TextEditorPresentationKey == FindOverlayPresentationFacts.PresentationKey);
00299 
00300                 if (presentationModel?.CompletedCalculation is not null)
00301                 {
00302                     var decorationByteChangedTargetTextSpanLocal = _decorationByteChangedTargetTextSpan;
00303                     if (decorationByteChangedTargetTextSpanLocal is not null)
00304                     {
00305                         var needsColorResetSinceNoLongerActive = presentationModel.CompletedCalculation.TextSpanList.FirstOrDefault(x =>
00306                             x.StartingIndexInclusive == decorationByteChangedTargetTextSpanLocal.Value.StartingIndexInclusive &&
00307                             x.EndingIndexExclusive == decorationByteChangedTargetTextSpanLocal.Value.EndingIndexExclusive &&
00308                             x.ResourceUri == decorationByteChangedTargetTextSpanLocal.Value.ResourceUri &&
00309                             x.GetText() == decorationByteChangedTargetTextSpanLocal.Value.GetText());
00310 
00311                         if (needsColorResetSinceNoLongerActive != default)
00312                         {
00313                             presentationModel.CompletedCalculation.TextSpanList = presentationModel.CompletedCalculation.TextSpanList.Replace(
00314                                 needsColorResetSinceNoLongerActive,
00315                                 needsColorResetSinceNoLongerActive with
00316                                 {
00317                                     DecorationByte = decorationByteChangedTargetTextSpanLocal.Value.DecorationByte
00318                                 });
00319                         }
00320                     }
00321 
00322                     var targetTextSpan = presentationModel.CompletedCalculation.TextSpanList[localActiveIndexMatchedTextSpan.Value];
00323                     _decorationByteChangedTargetTextSpan = targetTextSpan;
00324 
00325                     presentationModel.CompletedCalculation.TextSpanList =
00326                         presentationModel.CompletedCalculation.TextSpanList.Replace(targetTextSpan, targetTextSpan with
00327                         {
00328                             DecorationByte = (byte)FindOverlayDecorationKind.Insertion,
00329                         });
00330                 }
00331 
00332                 {
00333                     var decorationByteChangedTargetTextSpanLocal = _decorationByteChangedTargetTextSpan;
00334                     
00335                     if (decorationByteChangedTargetTextSpanLocal is not null)
00336                     {
00337                         TextEditorService.ViewModelApi.ScrollIntoView(
00338                             editContext,
00339                             modelModifier,                      
00340                             viewModelModifier,
00341                             decorationByteChangedTargetTextSpanLocal.Value);
00342                     }
00343                 }
00344                 
00345                 return Task.CompletedTask;
00346             });
