00058     public void StatusEnqueue()
00059     {
00060         _backgroundTaskService.Enqueue(
00061             Key<IBackgroundTask>.NewKey(),
00062             ContinuousBackgroundTaskWorker.GetQueueKey(),
00063             "git status -u",
00064             () =>
00065             {
00066                 var localGitState = _gitStateWrap.Value;
00067 
00068                 if (localGitState.Repo is null)
00069                     return Task.CompletedTask;
00070 
00071                 var gitStatusDashUCommand = $"{GitCliFacts.STATUS_COMMAND} -u";
00072                 var formattedCommand = new FormattedCommand(
00073                     GitCliFacts.TARGET_FILE_NAME,
00074                     new string[] { gitStatusDashUCommand })
00075                 {
00076                     HACK_ArgumentsString = gitStatusDashUCommand,
00077                     Tag = GitCliOutputParser.TagConstants.StatusEnqueue,
00078                 };
00079 
00080                 var terminalCommandRequest = new TerminalCommandRequest(
00081                     formattedCommand.Value,
00082                     localGitState.Repo.AbsolutePath.Value)
00083                 {
00084                     ContinueWithFunc = parsedCommand =>
00085                     {
00086                         var textSpanList = _gitCliOutputParser.StatusParseEntire(
00087                             parsedCommand.OutputCache.ToString());
00088                             
00089                         _gitCliOutputParser.DispatchSetStatusAction();
00090                             
00091                         parsedCommand.TextSpanList = textSpanList;
00092                         return Task.CompletedTask;
00093                     }
00094                 };
00095                     
00096                 _terminalStateWrap.Value.TerminalMap[TerminalFacts.GENERAL_KEY].EnqueueCommand(terminalCommandRequest);
00097                 return Task.CompletedTask;
00098             });
00099     }
