00191     public void AddEnqueue(GitRepo repoAtTimeOfRequest)
00192     {
00193         _backgroundTaskService.Enqueue(
00194             Key<IBackgroundTask>.NewKey(),
00195             ContinuousBackgroundTaskWorker.GetQueueKey(),
00196             "git add",
00197             () =>
00198             {
00199                 var localGitState = _gitStateWrap.Value;
00200 
00201                 if (localGitState.Repo is null || localGitState.Repo != repoAtTimeOfRequest)
00202                     return Task.CompletedTask;
00203 
00204                 var filesBuilder =  new StringBuilder();
00205         
00206                 foreach (var gitFile in localGitState.SelectedFileList)
00207                 {
00208                     var relativePathString = gitFile.RelativePathString;
00209         
00210                     if (_environmentProvider.DirectorySeparatorChar == '\\')
00211                     {
00212                         // The following fails (directory separator character):
00213                         //     git add ".\MyApp\"
00214                         //
00215                         // Whereas the following succeeds
00216                         //     git add "./MyApp/"
00217                         relativePathString = relativePathString.Replace(
00218                             _environmentProvider.DirectorySeparatorChar,
00219                             _environmentProvider.AltDirectorySeparatorChar);
00220                     }
00221         
00222                     filesBuilder.Append($"\"{relativePathString}\" ");
00223                 }
00224         
00225                 var terminalCommandArgs = "add " + filesBuilder.ToString();
00226         
00227                 var formattedCommand = new FormattedCommand(
00228                     GitCliFacts.TARGET_FILE_NAME,
00229                     new string[] { terminalCommandArgs })
00230                 {
00231                     HACK_ArgumentsString = terminalCommandArgs
00232                 };
00233                     
00234                 var terminalCommandRequest = new TerminalCommandRequest(
00235                     formattedCommand.Value,
00236                     localGitState.Repo.AbsolutePath.Value)
00237                 {
00238                     ContinueWithFunc = parsedCommand =>
00239                     {
00240                         StatusEnqueue();
00241                         return Task.CompletedTask;
00242                     }
00243                 };
00244                     
00245                 _terminalStateWrap.Value.TerminalMap[TerminalFacts.GENERAL_KEY].EnqueueCommand(terminalCommandRequest);
00246                 return Task.CompletedTask;
00247             });
00248     }
