00677                     formattedCommand.Value,
00678                     localGitState.Repo.AbsolutePath.Value)
00679                 {
00680                     ContinueWithFunc = gitHashParsedCommand =>
00681                     {
00682                         var hash = gitHashParsedCommand.OutputCache
00683                             .ToString()
00684                             .Substring(skipTextLength, takeTextLength)
00685                             .Trim();
00686                     
00687                         var showTerminalCommandRequest = new TerminalCommandRequest(
00688                             $"git show {hash}:{relativePathToFile}",
00689                             localGitState.Repo.AbsolutePath.Value)
00690                         {
00691                             ContinueWithFunc = parsedCommand =>
00692                             {
00693                                 var output = parsedCommand.OutputCache.ToString();
00694                             
00695                                 if (output.StartsWith(""))
00696                                     output = output["".Length..];
00697                             
00698                                 return callback.Invoke(
00699                                     _gitCliOutputParser,
00700                                     output);
00701                             }
00702                         };
00703                         
00704                         _terminalStateWrap.Value.TerminalMap[TerminalFacts.GENERAL_KEY].EnqueueCommand(showTerminalCommandRequest);
00705                         return Task.CompletedTask;
00706                     }
00707                 };
00708                     
00709                 _terminalStateWrap.Value.TerminalMap[TerminalFacts.GENERAL_KEY].EnqueueCommand(logTerminalCommandRequest);
00710                 return Task.CompletedTask;
00711             });
00712     }
00713     
00714     private List<int> GetPlusMarkedLineIndexList(string gitLogDashPOutput)
00715     {
00716         var stringWalker = new StringWalker(new ResourceUri("/getPlusMarkedLineIndexList.txt"), gitLogDashPOutput);
00717         var linesReadCount = 0;
00718         
00719         // The hunk header looks like:
00720         // "@@ -1,6 +1,23 @@"
00721         var atAtReadCount = 0;
00722         var oldAndNewHaveEqualFirstLine = false;
00723         int? sourceLineNumber = null;
00724         
00725         var isFirstCharacterOnLine = false;
00726         
00727         var plusMarkedLineIndexList = new List<int>();
00728         
00729         while (!stringWalker.IsEof)
00730         {
00731             if (WhitespaceFacts.LINE_ENDING_CHARACTER_LIST.Contains(stringWalker.CurrentCharacter))
00732             {
00733                 if (stringWalker.CurrentCharacter == '\r' && stringWalker.NextCharacter == '\n')
00734                     _ = stringWalker.ReadCharacter();
00735                     
00736                 linesReadCount++;
00737                 isFirstCharacterOnLine = true;
00738                 
00739                 if (sourceLineNumber is not null)
00740                     sourceLineNumber++;
00741             }
00742             else if (linesReadCount == 4 && sourceLineNumber is null)
00743             {
00744                 // Naively going to assume that the 5th line is always the start of the hunk header... for now
00745                 if (stringWalker.CurrentCharacter == '@' && stringWalker.NextCharacter == '@')
00746                 {
00747                     atAtReadCount++;
00748                     
00749                     if (atAtReadCount == 2)
00750                     {
00751                         if (WhitespaceFacts.LINE_ENDING_CHARACTER_LIST.Contains(stringWalker.PeekCharacter(2)))
00752                         {
00753                             // If immediately after the hunk header there is a newline character,
00754                             // then the old and new text are NOT equal with respects to the first line.
00755                         }
00756                         else
00757                         {
00758                             // If after the hunk header there is NOT a newline character,
00759                             // then the old and new text are equal with respects to the first line.
00760                             //
00761                             // (Does modificaton imply deletion of old, and insertion of new?)
00762                             oldAndNewHaveEqualFirstLine = true;
00763                         }
00764                         
00765                         sourceLineNumber = 0;
00766                     }
00767                 }
00768             }
00769             else if (sourceLineNumber is not null)
00770             {
00771                 if (isFirstCharacterOnLine && stringWalker.CurrentCharacter == '+')
00772                     plusMarkedLineIndexList.Add(sourceLineNumber.Value - 1);
00773             }
00774         
00775             _ = stringWalker.ReadCharacter();
00776         }
00777         
00778         return plusMarkedLineIndexList;
00779     }
00780     
00781     public void DiffFileEnqueue(
00782         GitRepo repoAtTimeOfRequest,
00783         string relativePathToFile,
00784         Func<GitCliOutputParser, string, List<int>, Task> callback)
00785     {
00786         _backgroundTaskService.Enqueue(
00787             Key<IBackgroundTask>.NewKey(),
00788             ContinuousBackgroundTaskWorker.GetQueueKey(),
00789             "git diff file",
00790             () =>
00791             {
00792                 var localGitState = _gitStateWrap.Value;
00793 
00794                 if (localGitState.Repo is null || localGitState.Repo != repoAtTimeOfRequest)
00795                     return Task.CompletedTask;
00796 
00797                 var terminalCommandArgs = $"diff -p {relativePathToFile}";
00798                 var formattedCommand = new FormattedCommand(
00799                     GitCliFacts.TARGET_FILE_NAME,
00800                     new string[] { terminalCommandArgs })
00801                 {
00802                     HACK_ArgumentsString = terminalCommandArgs,
00803                     Tag = GitCliOutputParser.TagConstants.LogFileEnqueue
00804                 };
00805 
00806                 var terminalCommandRequest = new TerminalCommandRequest(
00807                     formattedCommand.Value,
00808                     localGitState.Repo.AbsolutePath.Value)
00809                 {
00810                     ContinueWithFunc = parsedCommand =>
00811                     {
00812                         var plusMarkedLineIndexList = GetPlusMarkedLineIndexList(parsedCommand.OutputCache.ToString());
00813                     
00814                         return callback.Invoke(
00815                             _gitCliOutputParser,
00816                             parsedCommand.OutputCache.ToString(),
00817                             plusMarkedLineIndexList);
00818                     }
00819                 };
00820                     
00821                 _terminalStateWrap.Value.TerminalMap[TerminalFacts.GENERAL_KEY].EnqueueCommand(terminalCommandRequest);
00822                 return Task.CompletedTask;
00823             });
00824     }
00825 }
