00061     public Task DoCopyOnClick(MouseEventArgs arg)
00062     {
00063         var renderBatchLocal = _renderBatch;
00064         if (renderBatchLocal is null)
00065             return Task.CompletedTask;
00066         
00067         var model = renderBatchLocal.Model;
00068         var viewModel = renderBatchLocal.ViewModel;
00069 
00070         if (model is null || viewModel is null)
00071             return Task.CompletedTask;
00072 
00073         var commandArgs = ConstructCommandArgs(renderBatchLocal);
00074 
00075         _root.TextEditorService.PostUnique(
00076             nameof(TextEditorCommandDefaultFacts.Copy),
00077             editContext =>
00078             {
00079                 commandArgs.EditContext = editContext;
00080                 return TextEditorCommandDefaultFacts.Copy.CommandFunc
00081                     .Invoke(commandArgs);
00082             });
00083         return Task.CompletedTask;
00084     }
