00261     public async Task DoReloadOnClick(MouseEventArgs arg)
00262     {
00263         var renderBatchLocal = _renderBatch;
00264         if (renderBatchLocal is null)
00265             return;
00266         
00267         var model = renderBatchLocal.Model;
00268         var viewModel = renderBatchLocal.ViewModel;
00269 
00270         if (model is null || viewModel is null)
00271             return;
00272 
00273         var commandArgs = ConstructCommandArgs(renderBatchLocal);
00274         
00275         var dropdownKey = Key<DropdownRecord>.NewKey();
00276         
00277         var buttonDimensions = await _root.TextEditorService.JsRuntimeCommonApi
00278             .MeasureElementById(_reloadButtonHtmlElementId)
00279             .ConfigureAwait(false);
00280             
00281         var menuOptionList = new List<MenuOptionRecord>();
00282         
00283         var absolutePath = _root.EnvironmentProvider.AbsolutePathFactory(model.ResourceUri.Value, false);
00284 
00285         menuOptionList.Add(new MenuOptionRecord(
00286             "Cancel",
00287             MenuOptionKind.Read,
00288             OnClickFunc: () =>
00289             {
00290                 _root.Dispatcher.Dispatch(new DropdownState.DisposeAction(dropdownKey));
00291                 return Task.CompletedTask;
00292             }));
00293             
00294         menuOptionList.Add(new MenuOptionRecord(
00295             $"Reset: '{absolutePath.NameWithExtension}'",
00296             MenuOptionKind.Delete,
00297             OnClickFunc: () =>
00298             {
00299                 _root.TextEditorService.PostUnique(
00300                     nameof(DoReloadOnClick),
00301                     editContext =>
00302                     {
00303                         editContext.TextEditorService.ViewModelApi.Dispose(viewModel.ViewModelKey);
00304                         _root.Dispatcher.Dispatch(new DirtyResourceUriState.RemoveDirtyResourceUriAction(model.ResourceUri));
00305                         editContext.TextEditorService.ModelApi.Dispose(model.ResourceUri);
00306                         return Task.CompletedTask;
00307                     });
00308                 return Task.CompletedTask;
00309             }));
00310             
00311         var menu = new MenuRecord(menuOptionList.ToImmutableArray());
00312 
00313         var dropdownRecord = new DropdownRecord(
00314             dropdownKey,
00315             buttonDimensions.LeftInPixels,
00316             buttonDimensions.TopInPixels + buttonDimensions.HeightInPixels,
00317             typeof(MenuDisplay),
00318             new Dictionary<string, object?>
00319             {
00320                 {
00321                     nameof(MenuDisplay.MenuRecord),
00322                     menu
00323                 }
00324             },
00325             async () => await _root.TextEditorService.JsRuntimeCommonApi.FocusHtmlElementById(_reloadButtonHtmlElementId));
00326 
00327         _root.Dispatcher.Dispatch(new DropdownState.RegisterAction(dropdownRecord));
00328     }
