00338         if (model is null)
00339             return;
00340 
00341         var watchWindowObject = new WatchWindowObject(
00342             renderBatchLocal,
00343             typeof(TextEditorRenderBatchValidated),
00344             nameof(TextEditorRenderBatchValidated),
00345             true);
00346 
00347         var dialogRecord = new DialogViewModel(
00348             Key<IDynamicViewModel>.NewKey(),
00349             $"WatchWindow: {model.ResourceUri}",
00350             typeof(WatchWindowDisplay),
00351             new Dictionary<string, object?>
00352             {
00353                 {
00354                     nameof(WatchWindowDisplay.WatchWindowObject),
00355                     watchWindowObject
00356                 }
00357             },
00358             null,
00359             true,
00360             null);
00361 
00362         _root.DialogService.RegisterDialogRecord(dialogRecord);
