00020     public static HtmlSyntaxUnit ParseText(
00021         ResourceUri resourceUri,
00022         string content,
00023         InjectedLanguageDefinition? injectedLanguageDefinition = null)
00024     {
00025         var stringWalker = new StringWalker(resourceUri, content);
00026 
00027         var rootTagSyntaxBuilder = new TagNodeBuilder
00028         {
00029             OpenTagNameSyntax = new TagNameNode(
00030                 new TextEditorTextSpan(
00031                     0,
00032                     8,
00033                     (byte)HtmlDecorationKind.None,
00034                     ResourceUri.Empty,
00035                     "document")),
00036         };
00037 
00038         var textEditorHtmlDiagnosticBag = new DiagnosticBag();
00039 
00040         rootTagSyntaxBuilder.Children = HtmlSyntaxTreeStateMachine
00041             .ParseTagChildContent(
00042                 stringWalker,
00043                 textEditorHtmlDiagnosticBag,
00044                 injectedLanguageDefinition);
00045 
00046         var htmlSyntaxUnitBuilder = new HtmlSyntaxUnit.HtmlSyntaxUnitBuilder(
00047             rootTagSyntaxBuilder.Build(),
00048             textEditorHtmlDiagnosticBag);
00049 
00050         return htmlSyntaxUnitBuilder.Build();
