00056         public static IHtmlSyntaxNode ParseTag(
00057             StringWalker stringWalker,
00058             DiagnosticBag diagnosticBag,
00059             InjectedLanguageDefinition? injectedLanguageDefinition)
00060         {
00061             if (stringWalker.PeekForSubstring(
00062                     HtmlFacts.COMMENT_TAG_BEGINNING))
00063             {
00064                 return ParseComment(
00065                     stringWalker,
00066                     diagnosticBag,
00067                     injectedLanguageDefinition);
00068             }
00069 
00070             var startingPositionIndex = stringWalker.PositionIndex;
00071 
00072             var tagBuilder = new TagNodeBuilder();
00073 
00074             // HtmlFacts.TAG_OPENING_CHARACTER
00075             _ = stringWalker.ReadCharacter();
00076 
00077             // Example: <!DOCTYPE html>
00078             if (stringWalker.PeekCharacter(0) == HtmlFacts.SPECIAL_HTML_TAG)
00079             {
00080                 // HtmlFacts.SPECIAL_HTML_TAG_CHARACTER
00081                 stringWalker.ReadCharacter();
00082 
00083                 tagBuilder.HasSpecialHtmlCharacter = true;
00084             }
00085 
00086             tagBuilder.OpenTagNameSyntax = ParseTagName(
00087                 stringWalker,
00088                 diagnosticBag,
00089                 injectedLanguageDefinition);
00090 
00091             // Get all html attributes break when see End Of File or closing of the tag
00092             while (true)
00093             {
00094                 // Skip Whitespace
00095                 while (!stringWalker.IsEof)
00096                 {
00097                     if (WhitespaceFacts.ALL_LIST.Contains(stringWalker.CurrentCharacter))
00098                         _ = stringWalker.ReadCharacter();
00099                     else
00100                         break;
00101                 }
00102 
00103                 // End Of File is unexpected at this point so report a diagnostic.
00104                 if (stringWalker.CurrentCharacter == ParserFacts.END_OF_FILE)
00105                 {
00106                     diagnosticBag.ReportEndOfFileUnexpected(
00107                         new TextEditorTextSpan(
00108                             startingPositionIndex,
00109                             stringWalker.PositionIndex,
00110                             (byte)HtmlDecorationKind.Error,
00111                             stringWalker.ResourceUri,
00112                             stringWalker.SourceText));
00113 
00114                     return tagBuilder.Build();
00115                 }
00116 
00117                 if (stringWalker.PeekForSubstring(HtmlFacts.OPEN_TAG_WITH_CHILD_CONTENT_ENDING))
00118                 {
00119                     // Ending of opening tag
00120                     tagBuilder.HtmlSyntaxKind = HtmlSyntaxKind.TagOpeningNode;
00121 
00122                     // Skip the '>' character to set stringWalker at the first character of the child content
00123                     _ = stringWalker.ReadCharacter();
00124 
00125                     tagBuilder.Children = ParseTagChildContent(
00126                         stringWalker,
00127                         diagnosticBag,
00128                         injectedLanguageDefinition);
00129 
00130                     // TODO: check that the closing tag name matches the opening tag
00131                 }
00132                 else if (stringWalker.PeekForSubstring(HtmlFacts.OPEN_TAG_SELF_CLOSING_ENDING))
00133                 {
00134                     _ = stringWalker.ReadRange(
00135                             HtmlFacts.OPEN_TAG_SELF_CLOSING_ENDING
00136                                 .Length);
00137 
00138                     // Ending of self-closing tag
00139                     tagBuilder.HtmlSyntaxKind = HtmlSyntaxKind.TagSelfClosingNode;
00140 
00141                     return tagBuilder.Build();
00142                 }
00143                 else if (stringWalker.PeekForSubstring(HtmlFacts.CLOSE_TAG_WITH_CHILD_CONTENT_BEGINNING))
00144                 {
00145                     tagBuilder.HtmlSyntaxKind = HtmlSyntaxKind.TagClosingNode;
00146 
00147                     _ = stringWalker.ReadRange(
00148                         HtmlFacts.CLOSE_TAG_WITH_CHILD_CONTENT_BEGINNING
00149                             .Length);
00150 
00151                     var closeTagNameStartingPositionIndex = stringWalker.PositionIndex;
00152 
00153                     var closeTagNameBuilder = new StringBuilder();
00154 
00155                     while (!stringWalker.IsEof)
00156                     {
00157                         if (stringWalker.PeekForSubstring(HtmlFacts.CLOSE_TAG_WITH_CHILD_CONTENT_ENDING))
00158                         {
00159                             tagBuilder.CloseTagNameSyntax = new TagNameNode(
00160                                 new(closeTagNameStartingPositionIndex, stringWalker, (byte)HtmlDecorationKind.TagName));
00161 
00162                             _ = stringWalker.ReadRange(HtmlFacts.CLOSE_TAG_WITH_CHILD_CONTENT_ENDING.Length);
00163 
00164                             break;
00165                         }
00166 
00167                         closeTagNameBuilder.Append(stringWalker.CurrentCharacter);
00168 
00169                         _ = stringWalker.ReadCharacter();
00170                     }
00171 
00172                     if (tagBuilder.CloseTagNameSyntax is null)
00173                     {
00174                         // TODO: Not sure if this can happen but I am getting a warning about this and aim to get to this when I find time.
00175                         throw new NotImplementedException();
00176                     }
00177 
00178                     if (tagBuilder.OpenTagNameSyntax.TextEditorTextSpan.GetText() != tagBuilder.CloseTagNameSyntax.TextEditorTextSpan.GetText())
00179                     {
00180                         diagnosticBag.ReportOpenTagWithUnMatchedCloseTag(
00181                             tagBuilder.OpenTagNameSyntax.TextEditorTextSpan.GetText(),
00182                             tagBuilder.CloseTagNameSyntax.TextEditorTextSpan.GetText(),
00183                             new TextEditorTextSpan(
00184                                 closeTagNameStartingPositionIndex,
00185                                 stringWalker.PositionIndex,
00186                                 (byte)HtmlDecorationKind.Error,
00187                                 stringWalker.ResourceUri,
00188                                 stringWalker.SourceText));
00189                     }
00190 
00191                     return tagBuilder.Build();
00192                 }
00193                 else
00194                 {
00195                     var attributeSyntax = ParseAttribute(stringWalker, diagnosticBag, injectedLanguageDefinition);
00196                     tagBuilder.AttributeSyntaxes.Add(attributeSyntax);
00197                 }
00198             }
00199         }
