00202         public static TagNameNode ParseTagName(
00203             StringWalker stringWalker,
00204             DiagnosticBag diagnosticBag,
00205             InjectedLanguageDefinition? injectedLanguageDefinition)
00206         {
00207             var startingPositionIndex = stringWalker.PositionIndex;
00208 
00209             var tagNameBuilder = new StringBuilder();
00210 
00211             while (!stringWalker.IsEof)
00212             {
00213                 if (stringWalker.PeekForSubstringRange(HtmlFacts.TAG_NAME_STOP_DELIMITERS, out var matchedOn))
00214                     break;
00215 
00216                 tagNameBuilder.Append(stringWalker.CurrentCharacter);
00217 
00218                 _ = stringWalker.ReadCharacter();
00219             }
00220 
00221             var tagName = tagNameBuilder.ToString();
00222 
00223             if (tagNameBuilder.Length == 0)
00224             {
00225                 if (stringWalker.CurrentCharacter == ParserFacts.END_OF_FILE)
00226                 {
00227                     diagnosticBag.ReportEndOfFileUnexpected(
00228                         new TextEditorTextSpan(
00229                             startingPositionIndex,
00230                             stringWalker.PositionIndex,
00231                             (byte)HtmlDecorationKind.Error,
00232                             stringWalker.ResourceUri,
00233                             stringWalker.SourceText));
00234                 }
00235                 else
00236                 {
00237                     // Report a diagnostic for the missing 'tag name'
00238                     diagnosticBag.ReportTagNameMissing(
00239                         new TextEditorTextSpan(
00240                             startingPositionIndex,
00241                             stringWalker.PositionIndex,
00242                             (byte)HtmlDecorationKind.Error,
00243                             stringWalker.ResourceUri,
00244                             stringWalker.SourceText));
00245 
00246                     // Fabricate a value for the string variable: 'tagName' so the rest of the file can still be parsed.
00247                     tagName =
00248                         $"__{nameof(diagnosticBag.ReportTagNameMissing)}__";
00249                 }
00250             }
00251 
00252             var tagNameTextSpan = new TextEditorTextSpan(
00253                 startingPositionIndex,
00254                 stringWalker.PositionIndex,
00255                 (byte)HtmlDecorationKind.TagName,
00256                 stringWalker.ResourceUri,
00257                 stringWalker.SourceText);
00258 
00259             injectedLanguageDefinition?.ParseTagName?.Invoke(
00260                 stringWalker,
00261                 diagnosticBag,
00262                 injectedLanguageDefinition,
00263                 tagNameTextSpan);
00264 
00265             return new TagNameNode(tagNameTextSpan);
00266         }
