00268         public static List<IHtmlSyntax> ParseTagChildContent(
00269             StringWalker stringWalker,
00270             DiagnosticBag diagnosticBag,
00271             InjectedLanguageDefinition? injectedLanguageDefinition)
00272         {
00273             var startingPositionIndex = stringWalker.PositionIndex;
00274 
00275             List<IHtmlSyntax> htmlSyntaxes = new();
00276 
00277             int? textNodeStartingPositionIndex = null;
00278 
00279             // Make a TagTextSyntax - HTML TextNode if there was anything in the current builder
00280             void AddTextNode()
00281             {
00282                 if (textNodeStartingPositionIndex is null)
00283                     return;
00284 
00285                 var tagTextSyntax = new TextNode(
00286                     new TextEditorTextSpan(
00287                         textNodeStartingPositionIndex.Value,
00288                         stringWalker.PositionIndex,
00289                         (byte)GenericDecorationKind.None,
00290                         stringWalker.ResourceUri,
00291                         stringWalker.SourceText));
00292 
00293                 htmlSyntaxes.Add(tagTextSyntax);
00294                 textNodeStartingPositionIndex = null;
00295             }
00296 
00297             while (!stringWalker.IsEof)
00298             {
00299                 if (stringWalker.PeekForSubstring(HtmlFacts.CLOSE_TAG_WITH_CHILD_CONTENT_BEGINNING))
00300                     break;
00301 
00302                 if (stringWalker.CurrentCharacter == HtmlFacts.OPEN_TAG_BEGINNING)
00303                 {
00304                     // If there is text in textNodeBuilder add a new TextNode to the List of TagSyntax
00305                     AddTextNode();
00306 
00307                     if (stringWalker.PeekForSubstring(HtmlFacts.COMMENT_TAG_BEGINNING))
00308                     {
00309                         var node = ParseComment(stringWalker, diagnosticBag, injectedLanguageDefinition);
00310                         htmlSyntaxes.Add(node);
00311                     }
00312                     else
00313                     {
00314                         var node = ParseTag(stringWalker, diagnosticBag, injectedLanguageDefinition);
00315                         htmlSyntaxes.Add(node);
00316                     }
00317 
00318                     continue;
00319                 }
00320 
00321                 if (injectedLanguageDefinition is not null && stringWalker.AtInjectedLanguageCodeBlockTag(injectedLanguageDefinition))
00322                 {
00323                     // If there is text in textNodeBuilder add a new TextNode to the List of TagSyntax
00324                     AddTextNode();
00325 
00326                     var nodeBag = ParseInjectedLanguageCodeBlock(stringWalker, diagnosticBag, injectedLanguageDefinition);
00327                     htmlSyntaxes.AddRange(nodeBag);
00328 
00329                     continue;
00330                 }
00331 
00332                 textNodeStartingPositionIndex ??= stringWalker.PositionIndex;
00333 
00334                 _ = stringWalker.ReadCharacter();
00335             }
00336 
00337             if (stringWalker.CurrentCharacter == ParserFacts.END_OF_FILE)
00338                 diagnosticBag.ReportEndOfFileUnexpected(new(startingPositionIndex, stringWalker, (byte)HtmlDecorationKind.Error));
00339 
00340             // If there is text in textNodeBuilder add a new TextNode to the List of TagSyntax
00341             AddTextNode();
00342 
00343             return htmlSyntaxes;
00344         }
