00346         public static List<IHtmlSyntaxNode> ParseInjectedLanguageCodeBlock(
00347             StringWalker stringWalker,
00348             DiagnosticBag diagnosticBag,
00349             InjectedLanguageDefinition injectedLanguageDefinition)
00350         {
00351             var injectedLanguageFragmentSyntaxes = new List<IHtmlSyntaxNode>();
00352 
00353             var injectedLanguageFragmentSyntaxStartingPositionIndex = stringWalker.PositionIndex;
00354 
00355             // Track text span of the "@" sign (example in .razor files)
00356             injectedLanguageFragmentSyntaxes.Add(
00357                 new InjectedLanguageFragmentNode(
00358                     ImmutableArray<IHtmlSyntax>.Empty,
00359                     new TextEditorTextSpan(
00360                         injectedLanguageFragmentSyntaxStartingPositionIndex,
00361                         stringWalker.PositionIndex + 1,
00362                         (byte)HtmlDecorationKind.InjectedLanguageFragment,
00363                         stringWalker.ResourceUri,
00364                         stringWalker.SourceText)));
00365 
00366             injectedLanguageFragmentSyntaxes.AddRange(
00367                 injectedLanguageDefinition.ParseInjectedLanguageFunc
00368                     .Invoke(
00369                         stringWalker,
00370                         diagnosticBag,
00371                         injectedLanguageDefinition));
00372 
00373             return injectedLanguageFragmentSyntaxes;
00374         }
