00376         public static AttributeNode ParseAttribute(
00377             StringWalker stringWalker,
00378             DiagnosticBag diagnosticBag,
00379             InjectedLanguageDefinition? injectedLanguageDefinition)
00380         {
00381             var attributeNameSyntax = ParseAttributeName(
00382                 stringWalker,
00383                 diagnosticBag,
00384                 injectedLanguageDefinition);
00385 
00386             _ = TryReadAttributeValue(
00387                     stringWalker,
00388                     diagnosticBag,
00389                     injectedLanguageDefinition,
00390                     out var attributeValueSyntax);
00391 
00392             return new AttributeNode(
00393                 attributeNameSyntax,
00394                 attributeValueSyntax);
