00398         public static AttributeNameNode ParseAttributeName(
00399             StringWalker stringWalker,
00400             DiagnosticBag diagnosticBag,
00401             InjectedLanguageDefinition? injectedLanguageDefinition)
00402         {
00403             // When ParseAttributeName is invoked the PositionIndex is always 1 character too far
00404             _ = stringWalker.BacktrackCharacter();
00405 
00406             var startingPositionIndex = stringWalker.PositionIndex;
00407 
00408             bool firstLoop = true;
00409 
00410             while (!stringWalker.IsEof)
00411             {
00412                 _ = stringWalker.ReadCharacter();
00413 
00414                 // Try to read for injected language
00415                 if (firstLoop)
00416                 {
00417                     firstLoop = false;
00418 
00419                     if (injectedLanguageDefinition?.ParseAttributeName is not null &&
00420                         stringWalker.AtInjectedLanguageCodeBlockTag(injectedLanguageDefinition))
00421                     {
00422                         return injectedLanguageDefinition.ParseAttributeName
00423                             .Invoke(
00424                                 stringWalker,
00425                                 diagnosticBag,
00426                                 injectedLanguageDefinition);
00427                     }
00428                 }
00429 
00430                 if (WhitespaceFacts.ALL_LIST.Contains(stringWalker.CurrentCharacter) ||
00431                     HtmlFacts.SEPARATOR_FOR_ATTRIBUTE_NAME_AND_ATTRIBUTE_VALUE == stringWalker.CurrentCharacter ||
00432                     stringWalker.PeekForSubstringRange(HtmlFacts.OPEN_TAG_ENDING_OPTIONS, out var matchedOn))
00433                 {
00434                     break;
00435                 }
00436             }
00437 
00438             var attributeNameTextSpan = new TextEditorTextSpan(
00439                 startingPositionIndex,
00440                 stringWalker.PositionIndex,
00441                 (byte)HtmlDecorationKind.AttributeName,
00442                 stringWalker.ResourceUri,
00443                 stringWalker.SourceText);
00444 
00445             return new AttributeNameNode(attributeNameTextSpan);
00446         }
