00449         private static bool TryReadAttributeValue(
00450             StringWalker stringWalker,
00451             DiagnosticBag diagnosticBag,
00452             InjectedLanguageDefinition? injectedLanguageDefinition,
00453             out AttributeValueNode attributeValueSyntax)
00454         {
00455             if (WhitespaceFacts.ALL_LIST.Contains(stringWalker.CurrentCharacter))
00456             {
00457                 // Move to the first non-whitespace
00458                 while (!stringWalker.IsEof)
00459                 {
00460                     _ = stringWalker.ReadCharacter();
00461 
00462                     if (!WhitespaceFacts.ALL_LIST.Contains(stringWalker.CurrentCharacter))
00463                         break;
00464                 }
00465             }
00466 
00467             if (HtmlFacts.SEPARATOR_FOR_ATTRIBUTE_NAME_AND_ATTRIBUTE_VALUE == stringWalker.CurrentCharacter)
00468             {
00469                 attributeValueSyntax = ParseAttributeValue(
00470                     stringWalker,
00471                     diagnosticBag,
00472                     injectedLanguageDefinition);
00473 
00474                 return true;
00475             }
00476 
00477             // Set out variable as a 'matched attribute value' so there aren't any cascading error diagnostics due to having expected an attribute value.
00478             var attributeValueTextSpan = new TextEditorTextSpan(
00479                 0,
00480                 0,
00481                 (byte)HtmlDecorationKind.AttributeValue,
00482                 stringWalker.ResourceUri,
00483                 stringWalker.SourceText);
00484 
00485             attributeValueSyntax = new AttributeValueNode(attributeValueTextSpan);
00486 
00487             return false;
00488         }
