00575         public static CommentNode ParseComment(
00576             StringWalker stringWalker,
00577             DiagnosticBag diagnosticBag,
00578             InjectedLanguageDefinition? injectedLanguageDefinition)
00579         {
00580             // Suppress these unused parameters because all 'Parse...()' methods should take them for consistency.
00581             _ = diagnosticBag;
00582             _ = injectedLanguageDefinition;
00583 
00584             var startingPositionIndex = stringWalker.PositionIndex;
00585 
00586             while (!stringWalker.IsEof)
00587             {
00588                 _ = stringWalker.ReadCharacter();
00589 
00590                 if (stringWalker.PeekForSubstring(HtmlFacts.COMMENT_TAG_ENDING))
00591                     break;
00592             }
00593 
00594             // Skip the remaining characters in the comment tag ending string
00595             _ = stringWalker.ReadRange(HtmlFacts.COMMENT_TAG_ENDING.Length - 1);
00596 
00597             var commentTagTextSpan = new TextEditorTextSpan(
00598                 startingPositionIndex,
00599                 stringWalker.PositionIndex + 1,
00600                 (byte)HtmlDecorationKind.Comment,
00601                 stringWalker.ResourceUri,
00602                 stringWalker.SourceText);
00603 
00604             return new CommentNode(commentTagTextSpan);
00605         }
