00138     private async Task UpdateContent()
00139     {
00140         Console.WriteLine(nameof(UpdateContent));
00141     
00142         if (!TreeViewService.TryGetTreeViewContainer(
00143                 CodeSearchState.TreeViewCodeSearchContainerKey,
00144                 out var treeViewContainer))
00145         {
00146             Console.WriteLine("TryGetTreeViewContainer");
00147             return;
00148         }
00149         
00150         if (treeViewContainer.SelectedNodeList.Count > 1)
00151         {
00152             Console.WriteLine("treeViewContainer.SelectedNodeList.Count > 1");
00153             return;
00154         }
00155             
00156         var activeNode = treeViewContainer.ActiveNode;
00157         
00158         if (activeNode is not TreeViewCodeSearchTextSpan treeViewCodeSearchTextSpan)
00159         {
00160             Console.WriteLine("activeNode is not TreeViewCodeSearchTextSpan treeViewCodeSearchTextSpan");
00161             return;
00162         }
00163     
00164         var inPreviewViewModelKey = CodeSearchStateWrap.Value.PreviewViewModelKey;
00165         var outPreviewViewModelKey = Key<TextEditorViewModel>.NewKey();
00166 
00167         var filePath = treeViewCodeSearchTextSpan.Item.ResourceUri.Value;
00168         var resourceUri = treeViewCodeSearchTextSpan.Item.ResourceUri;
00169 
00170         if (TextEditorConfig.RegisterModelFunc is null)
00171             return;
00172 
00173         await TextEditorConfig.RegisterModelFunc.Invoke(
00174                 new RegisterModelArgs(resourceUri, ServiceProvider))
00175             .ConfigureAwait(false);
00176 
00177         if (TextEditorConfig.TryRegisterViewModelFunc is not null)
00178         {
00179             var viewModelKey = await TextEditorConfig.TryRegisterViewModelFunc.Invoke(new TryRegisterViewModelArgs(
00180                     outPreviewViewModelKey,
00181                     resourceUri,
00182                     new Category(nameof(CodeSearchDisplay)),
00183                     false,
00184                     ServiceProvider))
00185                 .ConfigureAwait(false);
00186 
00187             if (viewModelKey != Key<TextEditorViewModel>.Empty &&
00188                 TextEditorConfig.TryShowViewModelFunc is not null)
00189             {
00190                 Dispatcher.Dispatch(new CodeSearchState.WithAction(inState => inState with
00191                 {
00192                     PreviewFilePath = filePath,
00193                     PreviewViewModelKey = viewModelKey,
00194                 }));
00195 
00196                 if (inPreviewViewModelKey != Key<TextEditorViewModel>.Empty &&
00197                     inPreviewViewModelKey != viewModelKey)
00198                 {
00199                     TextEditorService.ViewModelApi.Dispose(inPreviewViewModelKey);
00200                 }
00201             }
00202         }
00203     }
