00061             out InMemoryEnvironmentProvider environmentProvider,
00062             out InMemoryFileSystemProvider fileSystemProvider,
00063             out ServiceProvider serviceProvider);
00064 
00065         var dirExists = await fileSystemProvider.Directory.ExistsAsync(WellKnownPaths.Directories.NonExistingDirectory);
00066         var fileExists = await fileSystemProvider.File.ExistsAsync(WellKnownPaths.Directories.NonExistingDirectory);
00067 
00068         Assert.False(dirExists && fileExists);
00069 
00070         await fileSystemProvider.Directory.CreateDirectoryAsync(WellKnownPaths.Directories.NonExistingDirectory);
00071 
00072         dirExists = await fileSystemProvider.Directory.ExistsAsync(WellKnownPaths.Directories.NonExistingDirectory);
00073         fileExists = await fileSystemProvider.File.ExistsAsync(WellKnownPaths.Directories.NonExistingDirectory);
00074 
00075         Assert.True(dirExists && !fileExists);
