00317 
00318             var childDirectoryPaths = await fileSystemProvider.Directory.GetDirectoriesAsync(WellKnownPaths.Directories.Homework);
00319 
00320             Assert.Contains(childDirectoryPaths, x => x == WellKnownPaths.Directories.Math);
00321             Assert.Contains(childDirectoryPaths, x => x == WellKnownPaths.Directories.Biology);
00322 
