00324             Assert.DoesNotContain(childDirectoryPaths, x => x == WellKnownPaths.Files.SubtractionTxt);
00325             Assert.DoesNotContain(childDirectoryPaths, x => x == WellKnownPaths.Files.NervousSystemTxt);
00326             Assert.DoesNotContain(childDirectoryPaths, x => x == WellKnownPaths.Files.SkeletalSystemTxt);
00327         }
