00330             Assert.True(await fileSystemProvider.Directory.ExistsAsync(WellKnownPaths.Directories.Root));
00331 
00332             var childDirectoryPaths = await fileSystemProvider.Directory.GetDirectoriesAsync(WellKnownPaths.Directories.Root);
00333 
00334             Assert.Contains(childDirectoryPaths, x => x == WellKnownPaths.Directories.Homework);
00335 
00336             Assert.DoesNotContain(childDirectoryPaths, x => x == WellKnownPaths.Directories.Math);
00337             Assert.DoesNotContain(childDirectoryPaths, x => x == WellKnownPaths.Directories.Biology);
00338 
00339             Assert.DoesNotContain(childDirectoryPaths, x => x == WellKnownPaths.Files.AdditionTxt);
