00038             string absolutePathString,
00039             CancellationToken cancellationToken = default)
00040         {
00041             try
00042             {
00043                 await _inMemoryFileSystemProvider._modificationSemaphore.WaitAsync().ConfigureAwait(false);
00044                 await UnsafeCreateDirectoryAsync(absolutePathString, cancellationToken).ConfigureAwait(false);
00045             }
00046             catch (Exception exception)
00047             {
00048                 NotifyUserOfException(exception);
00049                 throw;
00050             }
00051             finally
00052             {
