00168         public Task UnsafeCreateDirectoryAsync(
00169             string absolutePathString,
00170             CancellationToken cancellationToken = default)
00171         {
00172             var existingFile = _inMemoryFileSystemProvider._files.FirstOrDefault(f =>
00173                 f.AbsolutePath.Value == absolutePathString &&
00174                 f.IsDirectory);
00175 
00176             if (existingFile is not null)
00177                 return Task.CompletedTask;
00178 
00179             var absolutePath = _environmentProvider.AbsolutePathFactory(absolutePathString, true);
00180 
00181             var outDirectory = new InMemoryFile(
00182                 string.Empty,
00183                 absolutePath,
00184                 DateTime.UtcNow,
00185                 true);
00186 
00187             _inMemoryFileSystemProvider._files.Add(outDirectory);
00188 
00189             _environmentProvider.DeletionPermittedRegister(new SimplePath(
00190                 absolutePathString,
00191                 IS_DIRECTORY_RESPONSE));
00192 
00193             return Task.CompletedTask;
00194         }
