00196         public async Task UnsafeDeleteAsync(
00197             string absolutePathString,
00198             bool recursive,
00199             CancellationToken cancellationToken = default)
00200         {
00201             _environmentProvider.AssertDeletionPermitted(absolutePathString, IS_DIRECTORY_RESPONSE);
00202 
00203             var indexOfExistingFile = _inMemoryFileSystemProvider._files.FindIndex(f =>
00204                 f.AbsolutePath.Value == absolutePathString &&
00205                 f.IsDirectory);
00206 
00207             if (indexOfExistingFile == -1)
00208                 return;
00209 
00210             var childFileList = _inMemoryFileSystemProvider._files.Where(imf =>
00211                     imf.AbsolutePath.Value.StartsWith(absolutePathString) &&
00212                     imf.AbsolutePath.Value != absolutePathString)
00213                 .ToArray();
00214 
00215             foreach (var child in childFileList)
00216             {
00217                 if (child.IsDirectory)
00218                 {
00219                     await _inMemoryFileSystemProvider._directory.UnsafeDeleteAsync(
00220                             child.AbsolutePath.Value,
00221                             false,
00222                             cancellationToken)
00223                         .ConfigureAwait(false);
00224                 }
00225                 else
00226                 {
00227                     await _inMemoryFileSystemProvider._file.UnsafeDeleteAsync(
00228                             child.AbsolutePath.Value,
00229                             cancellationToken)
00230                         .ConfigureAwait(false);
00231                 }
00232             }
00233 
00234             _inMemoryFileSystemProvider._files.RemoveAt(indexOfExistingFile);
00235         }
