00237         public async Task UnsafeCopyAsync(
00238             string sourceAbsolutePathString,
00239             string destinationAbsolutePathString,
00240             CancellationToken cancellationToken = default)
00241         {
00242             var indexOfExistingFile = _inMemoryFileSystemProvider._files.FindIndex(f =>
00243                 f.AbsolutePath.Value == sourceAbsolutePathString &&
00244                 f.IsDirectory);
00245 
00246             if (indexOfExistingFile == -1)
00247                 return;
00248 
00249             var sourceAbsolutePath = _environmentProvider.AbsolutePathFactory(sourceAbsolutePathString, true);
00250 
00251             var childDirectories = (await GetDirectoriesAsync(sourceAbsolutePathString, cancellationToken).ConfigureAwait(false))
00252                 .Select(x => _environmentProvider.AbsolutePathFactory(x, true))
00253                 .ToArray();
00254 
00255             var childFiles = (await GetFilesAsync(sourceAbsolutePathString, cancellationToken).ConfigureAwait(false))
00256                 .Select(x => _environmentProvider.AbsolutePathFactory(x, false))
00257                 .ToArray();
00258 
00259             var children = childDirectories.Union(childFiles);
00260 
00261             var destinationExists = await UnsafeExistsAsync(destinationAbsolutePathString, cancellationToken).ConfigureAwait(false);
00262 
00263             if (destinationExists)
00264             {
00265                 _environmentProvider.AssertDeletionPermitted(destinationAbsolutePathString, IS_DIRECTORY_RESPONSE);
00266             }
00267             else
00268             {
00269                 var destinationAbsolutePath = _environmentProvider.AbsolutePathFactory(
00270                     destinationAbsolutePathString,
00271                     true);
00272 
00273                 var destinationFile = new InMemoryFile(
00274                     string.Empty,
00275                     destinationAbsolutePath,
00276                     DateTime.UtcNow,
00277                     true);
00278 
00279                 _inMemoryFileSystemProvider._files.Add(destinationFile);
00280             }
00281 
00282             foreach (var child in children)
00283             {
00284                 var innerDestinationPath = _environmentProvider.JoinPaths(
00285                     destinationAbsolutePathString,
00286                     sourceAbsolutePath.NameWithExtension);
00287 
00288                 var destinationChild = _environmentProvider.JoinPaths(
00289                     innerDestinationPath,
00290                     child.NameWithExtension);
00291 
00292                 if (child.IsDirectory)
00293                 {
00294                     await _inMemoryFileSystemProvider._directory.UnsafeCopyAsync(
00295                             child.Value,
00296                             innerDestinationPath,
00297                             cancellationToken)
00298                         .ConfigureAwait(false);
00299                 }
00300                 else
00301                 {
00302                     await _inMemoryFileSystemProvider._file.UnsafeCopyAsync(
00303                             child.Value,
00304                             destinationChild,
00305                             cancellationToken)
00306                         .ConfigureAwait(false);
00307                 }
00308             }
00309         }
