00314         public async Task UnsafeMoveAsync(
00315             string sourceAbsolutePathString,
00316             string destinationAbsolutePathString,
00317             CancellationToken cancellationToken = default)
00318         {
00319             _environmentProvider.AssertDeletionPermitted(sourceAbsolutePathString, IS_DIRECTORY_RESPONSE);
00320 
00321             var indexOfExistingFile = _inMemoryFileSystemProvider._files.FindIndex(f =>
00322                 f.AbsolutePath.Value == sourceAbsolutePathString &&
00323                 f.IsDirectory);
00324 
00325             if (indexOfExistingFile == -1)
00326                 return;
00327 
00328             if (await ExistsAsync(destinationAbsolutePathString).ConfigureAwait(false))
00329                 _environmentProvider.AssertDeletionPermitted(destinationAbsolutePathString, IS_DIRECTORY_RESPONSE);
00330 
00331             await UnsafeCopyAsync(sourceAbsolutePathString, destinationAbsolutePathString, cancellationToken).ConfigureAwait(false);
00332             await UnsafeDeleteAsync(sourceAbsolutePathString, true, cancellationToken).ConfigureAwait(false);
00333         }
