00335         public Task<string[]> UnsafeGetDirectoriesAsync(
00336             string absolutePathString,
00337             CancellationToken cancellationToken = default)
00338         {
00339             var existingFile = _inMemoryFileSystemProvider._files.FirstOrDefault(f =>
00340                 f.AbsolutePath.Value == absolutePathString &&
00341                 f.IsDirectory);
00342 
00343             if (existingFile is null)
00344                 return Task.FromResult(Array.Empty<string>());
00345 
00346             var childrenFromAllGenerationsList = _inMemoryFileSystemProvider._files.Where(
00347                 f => f.AbsolutePath.Value.StartsWith(absolutePathString) &&
00348                      f.AbsolutePath.Value != absolutePathString)
00349                 .ToArray();
00350 
00351             var directChildren = childrenFromAllGenerationsList.Where(
00352                 f =>
00353                 {
00354                     var withoutParentPrefix = new string(
00355                         f.AbsolutePath.Value
00356                             .Skip(absolutePathString.Length)
00357                             .ToArray());
00358 
00359                     return withoutParentPrefix.EndsWith("/") &&
00360                            withoutParentPrefix.Count(x => x == '/') == 1;
00361                 })
00362                 .Select(f => f.AbsolutePath.Value)
00363                 .ToArray();
00364 
00365             return Task.FromResult(directChildren);
00366         }
