00373                 f.AbsolutePath.Value == absolutePathString &&
00374                 f.IsDirectory);
00375 
00376             if (existingFile is null)
00377                 return Task.FromResult(Array.Empty<string>());
00378 
00379             var childrenFromAllGenerationsList = _inMemoryFileSystemProvider._files.Where(
00380                 f => f.AbsolutePath.Value.StartsWith(absolutePathString) &&
00381                      f.AbsolutePath.Value != absolutePathString);
00382 
00383             var directChildrenList = childrenFromAllGenerationsList.Where(
00384                 f =>
00385                 {
00386                     var withoutParentPrefix = new string(
00387                         f.AbsolutePath.Value
00388                             .Skip(absolutePathString.Length)
00389                             .ToArray());
00390 
00391                     return withoutParentPrefix.Count(x => x == '/') == 0;
00392                 })
