00413         private void NotifyUserOfException(Exception exception)
00414         {
00415             var title = "FILESYSTEM ERROR";
00416 
00417             if (exception.Message.StartsWith(PermittanceChecker.ERROR_PREFIX))
00418                 title = PermittanceChecker.ERROR_PREFIX;
00419 
00420             NotificationHelper.DispatchError(
00421                 title,
00422                 exception.ToString(),
00423                 _commonComponentRenderers,
00424                 _dispatcher,
00425                 TimeSpan.FromSeconds(10));
00426         }
