00039         public async Task DeleteAsync(
00040             string absolutePathString,
00041             CancellationToken cancellationToken = default)
00042         {
00043             try
00044             {
00045                 await _inMemoryFileSystemProvider._modificationSemaphore.WaitAsync().ConfigureAwait(false);
00046                 await UnsafeDeleteAsync(absolutePathString, cancellationToken).ConfigureAwait(false);
00047             }
00048             catch (Exception exception)
00049             {
00050                 NotifyUserOfException(exception);
00051                 throw;
00052             }
00053             finally
00054             {
00055                 _inMemoryFileSystemProvider._modificationSemaphore.Release();
00056             }
00057         }
