00151         public Task<bool> UnsafeExistsAsync(
00152             string absolutePathString,
00153             CancellationToken cancellationToken = default)
00154         {
00155             // System.InvalidOperationException: Collection was modified; enumeration operation may not execute.
00156             // Fix: added '.ToArray()' (2024-04-25)
00157             return Task.FromResult(_inMemoryFileSystemProvider._files.ToArray().Any(imf =>
00158                 imf.AbsolutePath.Value == absolutePathString &&
00159                 !imf.IsDirectory));
00160         }
