00162         public Task UnsafeDeleteAsync(
00163             string absolutePathString,
00164             CancellationToken cancellationToken = default)
00165         {
00166             _environmentProvider.AssertDeletionPermitted(absolutePathString, IS_DIRECTORY_RESPONSE);
00167 
00168             var indexOfExistingFile = _inMemoryFileSystemProvider._files.FindIndex(f =>
00169                 f.AbsolutePath.Value == absolutePathString &&
00170                 !f.IsDirectory);
00171 
00172             if (indexOfExistingFile == -1)
00173                 return Task.CompletedTask;
00174 
00175             _inMemoryFileSystemProvider._files.RemoveAt(indexOfExistingFile);
00176 
00177             return Task.CompletedTask;
00178         }
00179 
00180         public async Task UnsafeCopyAsync(
00181             string sourceAbsolutePathString,
00182             string destinationAbsolutePathString,
00183             CancellationToken cancellationToken = default)
00184         {
00185             // Source
00186             {
00187                 var indexOfSource = _inMemoryFileSystemProvider._files.FindIndex(f =>
00188                     f.AbsolutePath.Value == sourceAbsolutePathString &&
00189                     !f.IsDirectory);
00190 
00191                 if (indexOfSource == -1)
00192                     throw new LuthetusCommonException($"Source file: {sourceAbsolutePathString} was not found.");
00193             }
00194 
00195             // Destination
00196             { 
00197                 var indexOfDestination = _inMemoryFileSystemProvider._files.FindIndex(f =>
00198                     f.AbsolutePath.Value == destinationAbsolutePathString &&
00199                     !f.IsDirectory);
00200 
00201                 if (indexOfDestination != -1)
00202                     _environmentProvider.AssertDeletionPermitted(destinationAbsolutePathString, IS_DIRECTORY_RESPONSE);
00203             }
00204 
00205             var contents = await UnsafeReadAllTextAsync(
00206                     sourceAbsolutePathString,
00207                     cancellationToken)
00208                 .ConfigureAwait(false);
00209 
00210             await UnsafeWriteAllTextAsync(
00211                     destinationAbsolutePathString,
00212                     contents,
00213                     cancellationToken)
00214                 .ConfigureAwait(false);
00215         }
00216 
00217         public async Task UnsafeMoveAsync(
00218             string sourceAbsolutePathString,
00219             string destinationAbsolutePathString,
00220             CancellationToken cancellationToken = default)
00221         {
00222             _environmentProvider.AssertDeletionPermitted(sourceAbsolutePathString, IS_DIRECTORY_RESPONSE);
00223 
00224             if (await ExistsAsync(destinationAbsolutePathString).ConfigureAwait(false))
00225                 _environmentProvider.AssertDeletionPermitted(destinationAbsolutePathString, IS_DIRECTORY_RESPONSE);
00226 
00227             await UnsafeCopyAsync(
00228                     sourceAbsolutePathString,
00229                     destinationAbsolutePathString,
00230                     cancellationToken)
00231                 .ConfigureAwait(false);
00232 
00233             await UnsafeDeleteAsync(sourceAbsolutePathString, cancellationToken).ConfigureAwait(false);
00234         }
