00236         public Task<DateTime> UnsafeGetLastWriteTimeAsync(
00237             string absolutePathString,
00238             CancellationToken cancellationToken = default)
00239         {
00240             var existingFile = _inMemoryFileSystemProvider._files.FirstOrDefault(f =>
00241                 f.AbsolutePath.Value == absolutePathString &&
00242                 !f.IsDirectory);
00243 
00244             if (existingFile is null)
00245                 return Task.FromResult(default(DateTime));
00246 
00247             return Task.FromResult(existingFile.LastModifiedDateTime);
00248         }
