00250         public Task<string> UnsafeReadAllTextAsync(
00251             string absolutePathString,
00252             CancellationToken cancellationToken = default)
00253         {
00254             var existingFile = _inMemoryFileSystemProvider._files.FirstOrDefault(f =>
00255                 f.AbsolutePath.Value == absolutePathString &&
00256                 !f.IsDirectory);
00257 
00258             if (existingFile is null)
00259                 return Task.FromResult(string.Empty);
00260 
00261             return Task.FromResult(existingFile.Data);
00262         }
