00264         public Task UnsafeWriteAllTextAsync(
00265             string absolutePathString,
00266             string contents,
00267             CancellationToken cancellationToken = default)
00268         {
00269             var indexOfExistingFile = _inMemoryFileSystemProvider._files.FindIndex(f =>
00270                 f.AbsolutePath.Value == absolutePathString &&
00271                 !f.IsDirectory);
00272 
00273             if (indexOfExistingFile != -1)
00274             {
00275                 _environmentProvider.AssertDeletionPermitted(absolutePathString, IS_DIRECTORY_RESPONSE);
00276 
00277                 var existingFile = _inMemoryFileSystemProvider._files[indexOfExistingFile];
00278 
00279                 _inMemoryFileSystemProvider._files[indexOfExistingFile] = existingFile with
00280                 {
00281                     Data = contents
00282                 };
00283 
00284                 return Task.CompletedTask;
00285             }
00286 
00287             // Ensure Parent Directories Exist
00288             {
00289                 var parentDirectoryList = absolutePathString
00290                     .Split("/")
00291                     // The root directory splits into string.Empty
00292                     .Skip(1)
00293                     // Skip the file being written to itself
00294                     .SkipLast(1)
00295                     .ToArray();
00296 
00297                 var directoryPathBuilder = new StringBuilder("/");
00298 
00299                 for (int i = 0; i < parentDirectoryList.Length; i++)
00300                 {
00301                     directoryPathBuilder.Append(parentDirectoryList[i]);
00302                     directoryPathBuilder.Append("/");
00303 
00304                     _inMemoryFileSystemProvider._directory.UnsafeCreateDirectoryAsync(
00305                         directoryPathBuilder.ToString());
00306                 }
00307             }
00308 
00309             var absolutePath = _environmentProvider.AbsolutePathFactory(
00310                 absolutePathString,
00311                 false);
00312 
00313             var outFile = new InMemoryFile(
00314                 contents,
00315                 absolutePath,
00316                 DateTime.UtcNow,
00317                 false);
00318 
00319             _inMemoryFileSystemProvider._files.Add(outFile);
00320 
00321             _environmentProvider.DeletionPermittedRegister(
00322                 new SimplePath(absolutePathString, IS_DIRECTORY_RESPONSE));
00323 
00324             return Task.CompletedTask;
00325         }
