00327         private void NotifyUserOfException(Exception exception)
00328         {
00329             var title = "FILESYSTEM ERROR";
00330 
00331             if (exception.Message.StartsWith(PermittanceChecker.ERROR_PREFIX))
00332                 title = PermittanceChecker.ERROR_PREFIX;
00333 
00334             NotificationHelper.DispatchError(
00335                 title,
00336                 exception.ToString(),
00337                 _commonComponentRenderers,
00338                 _dispatcher,
00339                 TimeSpan.FromSeconds(10));
00340         }
