00028     public static bool IsWhitespaceCharacter(char character)
00029     {
00030         switch (character)
00031         {
00032             case WhitespaceCharacters.TAB:
00033             case WhitespaceCharacters.CARRIAGE_RETURN:
00034             case WhitespaceCharacters.NEW_LINE:
00035             case WhitespaceCharacters.SPACE:
00036                 return true;
00037             default:
00038                 return false;
00039         }
00040     }
