00085     public static char? MatchPunctuationCharacter(char character)
00086     {
00087         switch (character)
00088         {
00089             case PunctuationCharacters.OPEN_CURLY_BRACE:
00090                 return PunctuationCharacters.CLOSE_CURLY_BRACE;
00091             case PunctuationCharacters.CLOSE_CURLY_BRACE:
00092                 return PunctuationCharacters.OPEN_CURLY_BRACE;
00093             case PunctuationCharacters.OPEN_PARENTHESIS:
00094                 return PunctuationCharacters.CLOSE_PARENTHESIS;
00095             case PunctuationCharacters.CLOSE_PARENTHESIS:
00096                 return PunctuationCharacters.OPEN_PARENTHESIS;
00097             case PunctuationCharacters.OPEN_SQUARE_BRACKET:
00098                 return PunctuationCharacters.CLOSE_SQUARE_BRACKET;
00099             case PunctuationCharacters.CLOSE_SQUARE_BRACKET:
00100                 return PunctuationCharacters.OPEN_SQUARE_BRACKET;
00101             case PunctuationCharacters.OPEN_ARROW_BRACKET:
00102                 return PunctuationCharacters.CLOSE_ARROW_BRACKET;
00103             case PunctuationCharacters.CLOSE_ARROW_BRACKET:
00104                 return PunctuationCharacters.OPEN_ARROW_BRACKET;
00105             default:
00106                 return null;
00107         }
00108     }
