00110     public static int? DirectionToFindMatchingPunctuationCharacter(char character)
00111     {
00112         switch (character)
00113         {
00114             case PunctuationCharacters.OPEN_CURLY_BRACE:
00115                 return 1;
00116             case PunctuationCharacters.CLOSE_CURLY_BRACE:
00117                 return -1;
00118             case PunctuationCharacters.OPEN_PARENTHESIS:
00119                 return 1;
00120             case PunctuationCharacters.CLOSE_PARENTHESIS:
00121                 return -1;
00122             case PunctuationCharacters.OPEN_SQUARE_BRACKET:
00123                 return 1;
00124             case PunctuationCharacters.CLOSE_SQUARE_BRACKET:
00125                 return -1;
00126             case PunctuationCharacters.OPEN_ARROW_BRACKET:
00127                 return 1;
00128             case PunctuationCharacters.CLOSE_ARROW_BRACKET:
00129                 return -1;
00130             default:
00131                 return null;
00132         }
00133     }
