00157     public static bool CheckIsContextMenuEvent(
00158         string key, string code, bool shiftWasPressed, bool altWasPressed)
00159     {
00160         return key == "ContextMenu" ||
00161                CheckIsAlternateContextMenuEvent(key, code, shiftWasPressed, altWasPressed);
00162     }
