00198     public static char ConvertWhitespaceCodeToCharacter(string code)
00199     {
00200         switch (code)
00201         {
00202             case WhitespaceCodes.TAB_CODE:
00203                 return '\t';
00204             case WhitespaceCodes.ENTER_CODE:
00205                 return '\n';
00206             case WhitespaceCodes.SPACE_CODE:
00207                 return ' ';
00208             default:
00209                 throw new LuthetusCommonException($"Unrecognized Whitespace code of: {code}");
00210         }
00211     }
