00019     public static void LexNumericLiteralToken(
00020         StringWalker stringWalker, List<ISyntaxToken> syntaxTokens)
00021     {
00022         var entryPositionIndex = stringWalker.PositionIndex;
00023 
00024         // Declare outside the while loop to avoid overhead of redeclaring each loop? not sure
00025         var isNotANumber = false;
00026 
00027         while (!stringWalker.IsEof)
00028         {
00029             switch (stringWalker.CurrentCharacter)
00030             {
00031                 case '0':
00032                 case '1':
00033                 case '2':
00034                 case '3':
00035                 case '4':
00036                 case '5':
00037                 case '6':
00038                 case '7':
00039                 case '8':
00040                 case '9':
00041                     break;
00042                 default:
00043                     isNotANumber = true;
00044                     break;
00045             }
00046 
00047             if (isNotANumber)
00048                 break;
00049 
00050             _ = stringWalker.ReadCharacter();
00051         }
00052 
00053         var textSpan = new TextEditorTextSpan(
00054             entryPositionIndex,
00055             stringWalker.PositionIndex,
00056             (byte)GenericDecorationKind.None,
00057             stringWalker.ResourceUri,
00058             stringWalker.SourceText);
00059 
00060         syntaxTokens.Add(new NumericLiteralToken(textSpan));
00061     }
