00063     public static void LexCharLiteralToken(
00064         StringWalker stringWalker, List<ISyntaxToken> syntaxTokens, List<TextEditorTextSpan>? escapeCharacterList = null, char delimiter = '\'', char escapeCharacter = '\\')
00065     {
00066         var entryPositionIndex = stringWalker.PositionIndex;
00067 
00068         // Move past the opening delimiter
00069         _ = stringWalker.ReadCharacter();
00070 
00071         while (!stringWalker.IsEof)
00072         {
00073             if (stringWalker.CurrentCharacter == delimiter)
00074             {
00075                 _ = stringWalker.ReadCharacter();
00076                 break;
00077             }
00078             else if (stringWalker.CurrentCharacter == escapeCharacter)
00079             {
00080                 if (escapeCharacterList is not null)
00081                 {
00082                     escapeCharacterList.Add(new TextEditorTextSpan(
00083                         stringWalker.PositionIndex,
00084                         stringWalker.PositionIndex + 2,
00085                         (byte)GenericDecorationKind.EscapeCharacter,
00086                         stringWalker.ResourceUri,
00087                         stringWalker.SourceText));
00088                 }
00089 
00090                 // Presuming the escaped text is 2 characters,
00091                 // then read an extra character.
00092                 _ = stringWalker.ReadCharacter();
00093             }
00094 
00095             _ = stringWalker.ReadCharacter();
00096         }
00097 
00098         var textSpan = new TextEditorTextSpan(
00099             entryPositionIndex,
00100             stringWalker.PositionIndex,
00101             (byte)GenericDecorationKind.StringLiteral,
00102             stringWalker.ResourceUri,
00103             stringWalker.SourceText);
00104 
00105         syntaxTokens.Add(new CharLiteralToken(textSpan));
00106     }
