00189     public static void LexCommentMultiLineToken(
00190         StringWalker stringWalker, List<ISyntaxToken> syntaxTokens)
00191     {
00192         var entryPositionIndex = stringWalker.PositionIndex;
00193 
00194         // Move past the initial "/*"
00195         _ = stringWalker.ReadRange(2);
00196 
00197         // Declare outside the while loop to avoid overhead of redeclaring each loop? not sure
00198         var possibleClosingText = false;
00199         var sawClosingText = false;
00200 
00201         while (!stringWalker.IsEof)
00202         {
00203             switch (stringWalker.CurrentCharacter)
00204             {
00205                 case '*':
00206                     possibleClosingText = true;
00207                     break;
00208                 case '/':
00209                     if (possibleClosingText)
00210                         sawClosingText = true;
00211                     break;
00212                 default:
00213                     break;
00214             }
00215 
00216             _ = stringWalker.ReadCharacter();
00217 
00218             if (sawClosingText)
00219                 break;
00220         }
00221 
00222         var textSpan = new TextEditorTextSpan(
00223             entryPositionIndex,
00224             stringWalker.PositionIndex,
00225             (byte)GenericDecorationKind.CommentMultiLine,
00226             stringWalker.ResourceUri,
00227             stringWalker.SourceText);
00228 
00229         syntaxTokens.Add(new CommentMultiLineToken(textSpan));
