00232     public static void LexIdentifierOrKeywordOrKeywordContextual(
00233         StringWalker stringWalker, List<ISyntaxToken> syntaxTokens, LexerKeywords lexerKeywords)
00234     {
00235         var entryPositionIndex = stringWalker.PositionIndex;
00236 
00237         while (!stringWalker.IsEof)
00238         {
00239             if (!char.IsLetterOrDigit(stringWalker.CurrentCharacter) &&
00240                 stringWalker.CurrentCharacter != '_')
00241             {
00242                 break;
00243             }
00244 
00245             _ = stringWalker.ReadCharacter();
00246         }
00247 
00248         var textSpan = new TextEditorTextSpan(
00249             entryPositionIndex,
00250             stringWalker.PositionIndex,
00251             (byte)GenericDecorationKind.None,
00252             stringWalker.ResourceUri,
00253             stringWalker.SourceText);
00254 
00255         var textValue = textSpan.GetText();
00256 
00257         if (lexerKeywords.AllKeywords.Contains(textValue))
00258         {
00259             var decorationByte = (byte)GenericDecorationKind.Keyword;
00260 
00261             if (lexerKeywords.ControlKeywords.Contains(textValue))
00262                 decorationByte = (byte)GenericDecorationKind.KeywordControl;
00263 
00264             textSpan = textSpan with
00265             {
00266                 DecorationByte = decorationByte,
00267             };
00268 
00269             if (lexerKeywords.ContextualKeywords.Contains(textValue))
00270             {
00271                 syntaxTokens.Add(new KeywordContextualToken(textSpan, GetSyntaxKindForContextualKeyword(textSpan)));
00272                 return;
00273             }
00274 
00275             syntaxTokens.Add(new KeywordToken(textSpan, GetSyntaxKindForKeyword(textSpan)));
00276             return;
00277         }
00278 
00279         syntaxTokens.Add(new IdentifierToken(textSpan));
00280     }
