00755     public static SyntaxKind GetSyntaxKindForKeyword(TextEditorTextSpan textSpan)
00756     {
00757         var text = textSpan.GetText();
00758 
00759         switch (text)
00760         {
00761             case "abstract":
00762                 return SyntaxKind.AbstractTokenKeyword;
00763             case "as":
00764                 return SyntaxKind.AsTokenKeyword;
00765             case "base":
00766                 return SyntaxKind.BaseTokenKeyword;
00767             case "bool":
00768                 return SyntaxKind.BoolTokenKeyword;
00769             case "break":
00770                 return SyntaxKind.BreakTokenKeyword;
