00064             
00065         ProtectedPathList = ProtectedPathList.Add(new(
00066             SafeRoamingApplicationDataDirectoryAbsolutePath.Value,
00067             SafeRoamingApplicationDataDirectoryAbsolutePath.IsDirectory));
00068             
00069         ProtectedPathList = ProtectedPathList.Add(new(
00070             SafeLocalApplicationDataDirectoryAbsolutePath.Value,
00071             SafeLocalApplicationDataDirectoryAbsolutePath.IsDirectory));
00072 
00073         // Redundantly hardcode some obvious cases for protection.
00074         {
00075             ProtectedPathList = ProtectedPathList.Add(new SimplePath("/", true));
00076             ProtectedPathList = ProtectedPathList.Add(new SimplePath("\\", true));
00077             ProtectedPathList = ProtectedPathList.Add(new SimplePath("", true));
00078 
00079             try
00080             {
00081                 var entryAssembly = System.Reflection.Assembly.GetEntryAssembly();
00082 
00083                 if (entryAssembly is not null)
00084                 {
00085                     var driveExecutingFrom = Path.GetPathRoot(entryAssembly.Location);
00086 
00087                     if (driveExecutingFrom is not null)
00088                     {
00089                         if (driveExecutingFrom.EndsWith('/') || driveExecutingFrom.EndsWith('\\'))
00090                         {
00091                             DriveExecutingFromNoDirectorySeparator = driveExecutingFrom[..^1];
00092 
00093                             ProtectedPathList = ProtectedPathList.Add(
00094                                 new SimplePath(DriveExecutingFromNoDirectorySeparator + '/',
00095                                 true));
00096                             
00097                             ProtectedPathList = ProtectedPathList.Add(
00098                                 new SimplePath(DriveExecutingFromNoDirectorySeparator + '\\',
00099                                 true));
00100                         }
00101                         else
00102                         {
00103                             DriveExecutingFromNoDirectorySeparator = driveExecutingFrom;
00104                             ProtectedPathList = ProtectedPathList.Add(new SimplePath(driveExecutingFrom, true));
00105                         }
00106                     }
00107                 }
00108             }
00109             catch (Exception)
00110             {
00111                 // This code is intended to be an extra level of caution.
00112                 // it trys to add the current drive to the protected path list if possible.
00113             }
00114         }
00115     }
