00136     public string JoinPaths(string pathOne, string pathTwo)
00137     {
00138         if (IsDirectorySeparator(pathOne.LastOrDefault()))
00139             return pathOne + pathTwo;
00140 
00141         return string.Join(DirectorySeparatorChar, pathOne, pathTwo);
00142     }
